<div class="login-form">
  <form id="login">
    <h2 class="form__header">Авторизируйтесь</h2>
    <div class="divider-blue"></div>
    <div class="form-content">
      <div class="form-container">
        <div class="form__row">
          <label class="form__label">Логин
            <input type="text" placeholder="Введите логин" class="form__input">
          </label>
        </div>
        <div class="form__row">
          <label class="form__label">Пароль
            <input type="text" placeholder="Введите пароль" class="form__input">
          </label>
        </div>
        <div class="form__row button-row">
          <input type="submit" value="Войти" class="form__button form__button__submit">
        </div>
      </div>
    </div>
  </form>
</div>